    private void btnRegistrateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrateActionPerformed
             if(Cliente.tryConnection()==true){
              Registro.getRegistro().setVisible(true);
             }else{
                 javax.swing.JOptionPane.showMessageDialog(this,"VERIFICA TU CONEXION :( ","ERROR", 0);
             }
    }//GEN-LAST:event_btnRegistrateActionPerformed

    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        try {
            if(Cliente.tryConnection()==true){
              Cliente.sendServer(3,null);
              lblEstado.setText(Cliente.getMensaje());
             }
            else{
              lblEstado.setText("Error De Conexion");
            }
        } catch (Exception ex) {
            Logger.getLogger(Principal.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_formWindowOpened
      
    private String contraseña(){
      char [] pass =fieldContra.getPassword();
      String conversion=String.valueOf(pass);
      return conversion;
    }
    
    
   
