    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       double a= Double.parseDouble(jTextField11.getText());
       double u= Double.parseDouble(jTextField12.getText());
       int    n=Integer.parseInt(jTextField10.getText());
       
       if(u>a){
       double p=objeto.Ro(a, u);
       jTextField1.setText(Double.toString(p));
       
       double lq=objeto.Lq(p);
       jTextField2.setText(Double.toString(lq));
       
       double wq=objeto.Wq(lq, a);
       jTextField3.setText(Double.toString(wq));
       
       double w=objeto.W(wq, u);
       jTextField4.setText(Double.toString(w));
       
       double l=objeto.L(a, w);
       jTextField5.setText(Double.toString(l));
       
       double po=objeto.Po(p);
       jTextField6.setText(Double.toString(po));
       
       
       jTextField7.setText(Double.toString(p));
       
       
       double pn=objeto.Pn(p, n, po);
       jTextField8.setText(Double.toString(pn));
       
       
       jTextField9.setText(Double.toString(p));
       
       }
       else{
           javax.swing.JOptionPane.showMessageDialog(null,"Error 101 \"u>A\"  ");
       }
    }//GEN-LAST:event_jButton1ActionPerformed
  
